package javapractice1.ch3;

public class DataTypeEx8_MinMax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("byte_Min: " + Byte.MIN_VALUE + ", byte_Max: " + Byte.MAX_VALUE);
		System.out.println("short_Min: " + Short.MIN_VALUE + ", short_Max: " + Short.MAX_VALUE);
		System.out.println("int_Min: " + Integer.MIN_VALUE + ", int_Max: " + Integer.MAX_VALUE);
		System.out.println("long_Min: " + Long.MIN_VALUE + ", long_Max: " + Long.MAX_VALUE);
		System.out.println("float_Min: " + Float.MIN_VALUE + ", float_Max: " + Float.MAX_VALUE);
		System.out.println("double_Min: " + Double.MIN_VALUE + ", double_Max: " + Double.MAX_VALUE);

	}

}
