package javapractice1.wk3;

public class OperatorEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean awake = true;
		System.out.println(awake);
		
		awake = !awake;
		System.out.println(awake);
		
		awake = !awake;
		System.out.println(awake);
	}

}
