package javapractice1.wk3;

public class OperatorEx5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x1 = 1_000_000;
		int y1 = 1_000_000;
		int z1 = x1 * y1;
		System.out.println("(int)z1 is " + z1);
		
		long x2 = 1_000_000;
		long y2 = 1_000_000;
		long z2 = x2 * y2;
		System.out.println("(long)z2 is " + z2);

	}

}
